import React, { useState } from 'react'
import './CSS/LoginSigup.css'

const LoginSignup = () => {

  const [state,setState] = useState("Login");
  const  [formData,setFormData] = useState({
    username:"",
    password:"",
    email:""
  })

  const changeHandler = (e) =>{
    setFormData({...formData,[e.target.name]:e.target.value})
  }

  const Login = async() => {
    console.log("Login Function Executed",formData);
  }
  const signup = async() => {
    console.log("Signup Function Executed",formData);
    let responseData;
    await fetch('http://localhost:4000/signup',{
      method:'POST',
      headers:{
        Accept:'application/form-data',
        'Content-Type':'application/json',
      },
      body: JSON.stringify(FormData),
    }).then((response)=> response.json()).then((data)=>responseData=data)

    if (responseData.success){
      localStorage.setItem('auth-token',responseData.token);
      window.location.replace("/");
    }
  }

  return (
    <div className='loginsigup'>
        <div className="loginsigup-container">
          <h1>{state}</h1>
          <div className="loginsigup-fields">
            {state==="Sign Up"?<input name='username' value={formData.username} onChange={changeHandler} type="text" placeholder="Your Name" />:<></>}
            <input name='email' value={formData.email} onChange={changeHandler} type="email" placeholder="Email Address" />
            <input name='password' value={formData.password} onChange={changeHandler} type="password" placeholder="Password" />
          </div>
          <button onClick={()=>{state==="Login"?Login():signup()}}>{state}</button>
          {state==="Sign Up"
          ?<p className='loginsigup-login'>Already have an account? <span onClick={()=>{setState("Login")}}>Login Here</span></p>
          :<p className='loginsigup-login'>Create an account? <span onClick={()=>{setState("Sign Up")}}>Sign Up Here</span></p>}
          <div className="loginsigup-agree">
            <input type="checkbox" name='' id='' />
            <p>By continuing I agree to the terms of use and privacy policy.</p>
          </div>
        </div>
    </div>
  )
}

export default LoginSignup
